INSERT INTO workshop.st_guest (id, created_at, created_by, updated_at, updated_by, deleted_at, deleted_by, curator_id, guest_fio, guest_category, guest_photo, date, title, annotation, text, registration_link, webinar_link, youtube_link, vk_link, telegram_link, video, weight, icon, color, birth_date, birth_place) VALUES (5, 1600071629, 1, 1600447658, 1, null, null, 1, 'Лазо Виталий Иванович', 1, '5.jpg', 1599814800, 'альпинист-высотник', '<p>Виталий Иванович Лазо (род. <a href="https://ru.wikipedia.org/wiki/24_%D0%BC%D0%B0%D1%8F" title="24 мая">24 мая</a> <a href="https://ru.wikipedia.org/wiki/1973_%D0%B3%D0%BE%D0%B4" title="1973 год">1973</a>, <a href="https://ru.wikipedia.org/wiki/%D0%9F%D0%B5%D1%82%D1%80%D0%BE%D0%BF%D0%B0%D0%B2%D0%BB%D0%BE%D0%B2%D1%81%D0%BA-%D0%9A%D0%B0%D0%BC%D1%87%D0%B0%D1%82%D1%81%D0%BA%D0%B8%D0%B9" title="Петропавловск-Камчатский">Петропавловск-Камчатский</a>, <a href="https://ru.wikipedia.org/wiki/%D0%A1%D0%A1%D0%A1%D0%A0" class="mw-redirect" title="СССР">СССР</a>) — российский альпинист-высотник, <a href="https://ru.wikipedia.org/wiki/%D0%A1%D0%BA%D0%B8-%D0%B0%D0%BB%D1%8C%D0%BF%D0%B8%D0%BD%D0%B8%D0%B7%D0%BC" title="Ски-альпинизм">ски-альпинист</a>, <a href="https://ru.wikipedia.org/wiki/%D0%A4%D1%80%D0%B8%D1%80%D0%B0%D0%B9%D0%B4_(%D0%B3%D0%BE%D1%80%D0%BD%D1%8B%D0%B5_%D0%BB%D1%8B%D0%B6%D0%B8_%D0%B8_%D1%81%D0%BD%D0%BE%D1%83%D0%B1%D0%BE%D1%80%D0%B4)" title="Фрирайд (горные лыжи и сноуборд)">фрирайдер</a>, высотный экстрим-оператор, пилот <a href="https://ru.wikipedia.org/wiki/%D0%94%D1%80%D0%BE%D0%BD" class="mw-redirect" title="Дрон">дрона</a>, «<a href="https://ru.wikipedia.org/wiki/%D0%A1%D0%BD%D0%B5%D0%B6%D0%BD%D1%8B%D0%B9_%D0%B1%D0%B0%D1%80%D1%81_(%D1%82%D0%B8%D1%82%D1%83%D0%BB)" title="Снежный барс (титул)">Снежный барс</a>»<sup><a href="https://ru.wikipedia.org/wiki/%D0%9B%D0%B0%D0%B7%D0%BE,_%D0%92%D0%B8%D1%82%D0%B0%D0%BB%D0%B8%D0%B9_%D0%98%D0%B2%D0%B0%D0%BD%D0%BE%D0%B2%D0%B8%D1%87#cite_note-%D0%92%D0%B8%D1%82%D0%B0%D0%BB%D0%B8%D0%B9_%D0%9B%D0%B0%D0%B7%D0%BE-1">[1]</a></sup>.</p>', '<p><strong>11 сентября в 12:00</strong><strong> </strong>вместе с Дмитрием Кармановым<strong> </strong>в безопасном онлайн-формате вы встретитесь с <strong> </strong>руководителем спортивных российских и международных высотных экспедиций, лидером клуба «Горная территория», автором проекта «Фрирайд в зоне смерти», «снежным барсом» и профессиональным альпинистом-высотником <strong>Виталием Лазо.</strong></p><p><strong> </strong></p><p>Виталий Лазо поделится с нами тем, откуда ему удаётся черпать энергию, как проявить свои лидерские качества в жизни и обнажить лучшие черты характера у членов своей команды.</p>', 'https://anketolog.ru/s/392550/L2ZGRnVo', 'https://events.webinar.ru/14949509/6016015', 'https://youtu.be/Fn-nW5QUiSI', 'https://vk.com/tvoi_rostelecom', '', '', 50, 'user', 'success',1,'1');