INSERT INTO workshop.st_guest (id, created_at, created_by, updated_at, updated_by, deleted_at, deleted_by, curator_id, guest_fio, guest_category, guest_photo, date, birth_date, birth_place, title, annotation, text, registration_link, webinar_link, youtube_link, vk_link, telegram_link, video, weight, icon, color) VALUES (1, 1600071629, 1, 1600465492, 1, null, null, 1, 'Рязанский Сергей Николаевич', 2, '1.jpg', 1560841200, 153532800, 'Москва', 'космонавт-испытатель', '<p>Серге́й Никола́евич Ряза́нский (род. <a href="https://ru.wikipedia.org/wiki/13_%D0%BD%D0%BE%D1%8F%D0%B1%D1%80%D1%8F" title="13 ноября">13 ноября</a> <a href="https://ru.wikipedia.org/wiki/1974" class="mw-redirect" title="">1974</a>, <a href="https://ru.wikipedia.org/wiki/%D0%9C%D0%BE%D1%81%D0%BA%D0%B2%D0%B0" title="Москва">Москва</a>) — российский <a href="https://ru.wikipedia.org/wiki/%D0%9A%D0%BE%D1%81%D0%BC%D0%BE%D0%BD%D0%B0%D0%B2%D1%82" title="Космонавт">космонавт</a>-испытатель отряда космонавтов <a href="https://ru.wikipedia.org/wiki/%D0%A0%D0%BE%D1%81%D0%BA%D0%BE%D1%81%D0%BC%D0%BE%D1%81" title="Роскосмос">Роскосмоса</a><sup><a href="https://ru.wikipedia.org/wiki/%D0%A0%D1%8F%D0%B7%D0%B0%D0%BD%D1%81%D0%BA%D0%B8%D0%B9,_%D0%A1%D0%B5%D1%80%D0%B3%D0%B5%D0%B9_%D0%9D%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0%D0%B5%D0%B2%D0%B8%D1%87#cite_note-%D0%A6%D0%9F%D0%9A-1">[1]</a></sup>. 535-й космонавт мира и 117-й космонавт России<sup><a href="https://ru.wikipedia.org/wiki/%D0%A0%D1%8F%D0%B7%D0%B0%D0%BD%D1%81%D0%BA%D0%B8%D0%B9,_%D0%A1%D0%B5%D1%80%D0%B3%D0%B5%D0%B9_%D0%9D%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0%D0%B5%D0%B2%D0%B8%D1%87#cite_note-astronaut.ru-2">[2]</a></sup>. <a href="https://ru.wikipedia.org/wiki/%D0%93%D0%B5%D1%80%D0%BE%D0%B9_%D0%A0%D0%BE%D1%81%D1%81%D0%B8%D0%B9%D1%81%D0%BA%D0%BE%D0%B9_%D0%A4%D0%B5%D0%B4%D0%B5%D1%80%D0%B0%D1%86%D0%B8%D0%B8" title="Герой Российской Федерации">Герой Российской Федерации</a>, <a href="https://ru.wikipedia.org/wiki/%D0%9B%D1%91%D1%82%D1%87%D0%B8%D0%BA-%D0%BA%D0%BE%D1%81%D0%BC%D0%BE%D0%BD%D0%B0%D0%B2%D1%82_%D0%A0%D0%BE%D1%81%D1%81%D0%B8%D0%B9%D1%81%D0%BA%D0%BE%D0%B9_%D0%A4%D0%B5%D0%B4%D0%B5%D1%80%D0%B0%D1%86%D0%B8%D0%B8" title="Лётчик-космонавт Российской Федерации">лётчик-космонавт Российской Федерации</a> (2015)<sup><a href="https://ru.wikipedia.org/wiki/%D0%A0%D1%8F%D0%B7%D0%B0%D0%BD%D1%81%D0%BA%D0%B8%D0%B9,_%D0%A1%D0%B5%D1%80%D0%B3%D0%B5%D0%B9_%D0%9D%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0%D0%B5%D0%B2%D0%B8%D1%87#cite_note-3">[3]</a></sup>. Первый в мире учёный — командир космического корабля<sup><a href="https://ru.wikipedia.org/wiki/%D0%A0%D1%8F%D0%B7%D0%B0%D0%BD%D1%81%D0%BA%D0%B8%D0%B9,_%D0%A1%D0%B5%D1%80%D0%B3%D0%B5%D0%B9_%D0%9D%D0%B8%D0%BA%D0%BE%D0%BB%D0%B0%D0%B5%D0%B2%D0%B8%D1%87#cite_note-%D0%A6%D0%9F%D0%9A-1">[1]</a></sup>.</p>', '<p>У нас <strong>18 июля с 10 до 12 часов  в конференц зале (В 140 – 141) на первом этаже в Комсити </strong>будет классное мероприятие с приглашенным <strong>летчиком-испытателем Сергеем Рязанским</strong>, который расскажет свою историю успеха и поделиться знаниями о том, как быть лидером, сохранять самообладание и управлять командой в самых неординарных ситуациях.</p>', '', '', '', 'https://vk.com/tvoi_rostelecom', '', '1.mp4', 10, 'user', 'success');
INSERT INTO workshop.st_guest (id, created_at, created_by, updated_at, updated_by, deleted_at, deleted_by, curator_id, guest_fio, guest_category, guest_photo, date, birth_date, birth_place, title, annotation, text, registration_link, webinar_link, youtube_link, vk_link, telegram_link, video, weight, icon, color) VALUES (2, 1600071629, 1, 1600465727, 1, null, null, 1, 'Ягудин Алексей Константинович', 1, '2.jpg', 1571209200, 322185600, 'Ленинград', 'фигурист', '<p>Алексе́й Константи́нович Ягу́дин (род. <a href="https://ru.wikipedia.org/wiki/18_%D0%BC%D0%B0%D1%80%D1%82%D0%B0" title="18 марта">18 марта</a> <a href="https://ru.wikipedia.org/wiki/1980" class="mw-redirect" title="1980">1980</a>, <a href="https://ru.wikipedia.org/wiki/%D0%A1%D0%B0%D0%BD%D0%BA%D1%82-%D0%9F%D0%B5%D1%82%D0%B5%D1%80%D0%B1%D1%83%D1%80%D0%B3" title="Санкт-Петербург">Ленинград</a>) — <a href="https://ru.wikipedia.org/wiki/%D0%A0%D0%BE%D1%81%D1%81%D0%B8%D1%8F" title="Россия">российский</a> <a href="https://ru.wikipedia.org/wiki/%D0%A4%D0%B8%D0%B3%D1%83%D1%80%D0%BD%D0%BE%D0%B5_%D0%BA%D0%B0%D1%82%D0%B0%D0%BD%D0%B8%D0%B5" title="Фигурное катание">фигурист</a>, <a href="https://ru.wikipedia.org/wiki/%D0%97%D0%B0%D1%81%D0%BB%D1%83%D0%B6%D0%B5%D0%BD%D0%BD%D1%8B%D0%B9_%D0%BC%D0%B0%D1%81%D1%82%D0%B5%D1%80_%D1%81%D0%BF%D0%BE%D1%80%D1%82%D0%B0_%D0%A0%D0%BE%D1%81%D1%81%D0%B8%D0%B8" title="Заслуженный мастер спорта России">заслуженный мастер спорта России</a>. <a href="https://ru.wikipedia.org/wiki/%D0%97%D0%B8%D0%BC%D0%BD%D0%B8%D0%B5_%D0%9E%D0%BB%D0%B8%D0%BC%D0%BF%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D0%B5_%D0%B8%D0%B3%D1%80%D1%8B_2002" title="Зимние Олимпийские игры 2002">Олимпийский чемпион</a> <a href="https://ru.wikipedia.org/wiki/%D0%A4%D0%B8%D0%B3%D1%83%D1%80%D0%BD%D0%BE%D0%B5_%D0%BA%D0%B0%D1%82%D0%B0%D0%BD%D0%B8%D0%B5_%D0%BD%D0%B0_%D0%B7%D0%B8%D0%BC%D0%BD%D0%B8%D1%85_%D0%9E%D0%BB%D0%B8%D0%BC%D0%BF%D0%B8%D0%B9%D1%81%D0%BA%D0%B8%D1%85_%D0%B8%D0%B3%D1%80%D0%B0%D1%85_2002_%E2%80%94_%D0%BE%D0%B4%D0%B8%D0%BD%D0%BE%D1%87%D0%BD%D0%BE%D0%B5_%D0%BA%D0%B0%D1%82%D0%B0%D0%BD%D0%B8%D0%B5_(%D0%BC%D1%83%D0%B6%D1%87%D0%B8%D0%BD%D1%8B)" title="Фигурное катание на зимних Олимпийских играх 2002 — одиночное катание (мужчины)">2002 года</a>, четырёхкратный <a href="https://ru.wikipedia.org/wiki/%D0%A7%D0%B5%D0%BC%D0%BF%D0%B8%D0%BE%D0%BD%D0%B0%D1%82_%D0%BC%D0%B8%D1%80%D0%B0_%D0%BF%D0%BE_%D1%84%D0%B8%D0%B3%D1%83%D1%80%D0%BD%D0%BE%D0%BC%D1%83_%D0%BA%D0%B0%D1%82%D0%B0%D0%BD%D0%B8%D1%8E" title="Чемпионат мира по фигурному катанию">чемпион мира</a> (1998, 1999, 2000 и 2002 годы), трёхкратный <a href="https://ru.wikipedia.org/wiki/%D0%A7%D0%B5%D0%BC%D0%BF%D0%B8%D0%BE%D0%BD%D0%B0%D1%82_%D0%95%D0%B2%D1%80%D0%BE%D0%BF%D1%8B_%D0%BF%D0%BE_%D1%84%D0%B8%D0%B3%D1%83%D1%80%D0%BD%D0%BE%D0%BC%D1%83_%D0%BA%D0%B0%D1%82%D0%B0%D0%BD%D0%B8%D1%8E" title="Чемпионат Европы по фигурному катанию">чемпион Европы</a> (1998, 1999, 2002 годы), двукратный победитель <a href="https://ru.wikipedia.org/wiki/%D0%A4%D0%B8%D0%BD%D0%B0%D0%BB_%D0%93%D1%80%D0%B0%D0%BD-%D0%BF%D1%80%D0%B8_%D0%BF%D0%BE_%D1%84%D0%B8%D0%B3%D1%83%D1%80%D0%BD%D0%BE%D0%BC%D1%83_%D0%BA%D0%B0%D1%82%D0%B0%D0%BD%D0%B8%D1%8E" title="Финал Гран-при по фигурному катанию">финалов Гран-при по фигурному катанию</a>, двукратный чемпион мира среди профессионалов и чемпион <a href="https://ru.wikipedia.org/wiki/%D0%A7%D0%B5%D0%BC%D0%BF%D0%B8%D0%BE%D0%BD%D0%B0%D1%82_%D0%BC%D0%B8%D1%80%D0%B0_%D0%BF%D0%BE_%D1%84%D0%B8%D0%B3%D1%83%D1%80%D0%BD%D0%BE%D0%BC%D1%83_%D0%BA%D0%B0%D1%82%D0%B0%D0%BD%D0%B8%D1%8E_%D1%81%D1%80%D0%B5%D0%B4%D0%B8_%D1%8E%D0%BD%D0%B8%D0%BE%D1%80%D0%BE%D0%B2_1996" title="Чемпионат мира по фигурному катанию среди юниоров 1996">мира среди юниоров.</a> Введен в зал славы фигурного катания в США в Колорадо-Спрингс.</p>', '<p>Нужно написать текст</p>', '', '', '', 'https://vk.com/tvoi_rostelecom', '', '2.mp4', 20, 'user', 'success');
